<script type="text/javascript">
    var base_url = "<?php echo base_url()?>";

    //testimonials
    function transitionBox(from, to) {
        function next() {
            var nextTo;
            if (to.is(":last-child")) {
                nextTo = to.closest(".boxes").children("div").first();
            } else {
                nextTo = to.next();
            }
            to.fadeIn(500, function () {
                setTimeout(function () {
                    transitionBox(to, nextTo);
                }, 5000);
            });
        }
        
        if (from) {
            from.fadeOut(500, next);
        } else {
            next();
        }
    }


    $(document).ready(function(e) {

        $('.icon-tabs').click(function(e){
            e.preventDefault();
            window.open($(this).attr('href'));
        });

        //testimonials
        var allBoxes = $("div.boxes").children("div");
        transitionBox(null, allBoxes.first());
        var num = 0;

        $('.no-image').click(function(e){
            e.preventDefault();
        });

        $('.everytime').each(function(e){

            num = $(this).attr('g-num');

            if($(this).attr('g-type') == "photo" )
            {
                $('.fancybox'+num).fancybox();
            }  
            else
            {
                $('.fancybox-media'+num)
                    .attr('rel', 'media-gallery')
                    .fancybox({
                    prevEffect: 'none',
                    nextEffect: 'none',
                    closeBtn: true,
                    arrows: false,
                        helpers : {
                           // thumbs: {
                            //    width: 50,
                            //    height: 50
                            //}, // requires to include thumbs js and css files
                            media: {}, // requires to include media js file
                            buttons: {} // requires to include buttons js and css files
                        }
                });                
            }

        });


        $('#test').scrollToFixed();
        $('.res-nav_click').click(function(){
            $('.main-nav').slideToggle();
            return false    
            
        });

        $('#text-name').focus(function(){
            $(this).css('background-color','#FFFFFF');
        });

        $('#text-email').focus(function(){
            $(this).css('background-color','#FFFFFF');
        });

        $('#text-message').focus(function(){
            $(this).css('background-color','#FFFFFF');
        });

        $('#submit-btn').click(function(e){
            e.preventDefault();
            var name = $('#text-name').val();
            var email = $('#text-email').val();
            var message = $('#text-message').val();
           
            //check the fields
            if(name != "" && email != "" && message != "")
            {
                $.ajax({
                    type: "POST",
                    url: base_url + "email/send",
                    data: { name:name, email:email, message:message },
                    asyc: false,
                    cache: false,
                    beforeSend : function (){
                        $.blockUI({ message: "<h5><img src='"+base_url+"img/busy.gif' /> Just a moment...</h5>" });
                    },              
                    success:
                        function(data)
                        {   
                            console.log(data);
                            if(data === "true")
                            {
                                alert("message sent");
                                $('#text-name').val("");
                                $('#text-email').val("");
                                $('#text-message').val("");
                            }
                            else if(data === "false")
                            {
                                alert("something went wrong");
                            }
                            else
                            {
                                alert(data);
                            }
                        },
                    complete : function (){
                        $.unblockUI();
                    }

                });              
            }
            else
            {
                //alert("wrong");
                if(name == "")
                {
                    $('#text-name').css('background-color','#f2dede');
                }
                if(email == "")
                {
                    $('#text-email').css('background-color','#f2dede');
                }
                if(message == "")
                {
                    $('#text-message').css('background-color','#f2dede');    
                }
                
            }
        
        });

    });
</script>

